import reimport pyarabic.araby as ardef clean_data(text):    '''    A function to be used in the FastAPI code to do cleaning for any text    Parameters    ----------    text : str        Raw text.    Returns    -------    text : str        Clean text.    '''    #Remove emojis, non-Arabic characters and numbers    text = re.sub('([@A-Za-z0-9]+)|[^\w\s]|#|http\S+', '', text).strip()    #Remove Tashkeel    text = ar.strip_tashkeel(text)    #Remove Tatweel    text = ar.strip_tatweel(text)    #Replace Taa Marbota with Haa to avoid some misspellings    text = text.replace("ة","ه")    #Replace All Alefs with standard Alef wasl to normalize Alef and Hamza    text = re.sub('[إأآ]', 'ا', text)    #Replace All Alf Maksor with Yaa to anormalize it in all writings    text = re.sub('[ى]', 'ي', text)    return text